/**
 * Remove default templates
 */
function tora_lc_remove_templates( $templates ) {
	if ( is_array( $templates ) ) {
		foreach ( $templates as $id => $template ) {
			if ( $template['section'] == 'original' ) {
				unset( $templates[$id] );
			}
		}
	}
	return $templates;
} 
add_filter( 'dslc_get_templates', 'tora_lc_remove_templates' );

